import{c as t}from"./app.eeb96852.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=t(`<h1 id="\u7B2Ci\u90E8\u5206-c-\u57FA\u7840\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u7B2Ci\u90E8\u5206-c-\u57FA\u7840\u5185\u5BB9" aria-hidden="true">#</a> \u7B2C\u2160\u90E8\u5206 C++\u57FA\u7840\u5185\u5BB9</h1><p>\u4EFB\u4F55\u5E38\u7528\u7684\u7F16\u7A0B\u8BED\u8A00\u90FD\u5177\u5907\u4E00\u7EC4\u516C\u5171\u7684\u8BED\u6CD5\u7279\u5F81\uFF0C\u4E0D\u540C\u8BED\u8A00\u4EC5\u5728\u7279\u5F81\u7684\u7EC6\u8282\u4E0A\u6709\u6240\u533A\u522B\u3002\u8981\u60F3\u5B66\u4E60\u5E76\u638C\u63E1\u4E00\u79CD\u7F16\u7A0B\u8BED\u8A00\uFF0C\u7406\u89E3\u5176\u8BED\u6CD5\u7279\u5F81\u7684\u5B9E\u73B0\u7EC6\u8282\u662F\u7B2C\u4E00\u6B65\u3002\u6700\u57FA\u672C\u7684\u7279\u5F81\u5305\u62EC\uFF1A</p><ul><li>\u6574\u578B\u3001\u5B57\u7B26\u578B\u7B49\u5185\u7F6E\u7C7B\u578B</li><li>\u53D8\u91CF\uFF0C\u7528\u6765\u4E3A\u5BF9\u8C61\u547D\u540D</li><li>\u8868\u8FBE\u5F0F\u548C\u8BED\u53E5\uFF0C\u7528\u4E8E\u64CD\u7EB5\u4E0A\u8FF0\u6570\u636E\u7C7B\u578B\u7684\u5177\u4F53\u503C</li><li>if\u6216while\u7B49\u63A7\u5236\u7ED3\u6784\uFF0C\u8FD9\u4E9B\u7ED3\u6784\u5141\u8BB8\u6211\u4EEC\u6709\u9009\u62E9\u5730\u6267\u884C\u4E00\u4E9B\u8BED\u53E5\u6216\u8005\u91CD\u590D\u5730\u6267\u884C\u4E00\u4E9B\u8BED\u53E5</li><li>\u51FD\u6570\uFF0C\u7528\u4E8E\u5B9A\u4E49\u53EF\u4F9B\u968F\u65F6\u8C03\u7528\u7684\u8BA1\u7B97\u5355\u5143</li></ul><p>\u5927\u591A\u6570\u7F16\u7A0B\u8BED\u8A00\u901A\u8FC7\u4E24\u79CD\u65B9\u5F0F\u6765\u8FDB\u4E00\u6B65\u8865\u5145\u5176\u57FA\u672C\u7279\u5F81\uFF1A\u4E00\u662F\u8D4B\u4E88\u7A0B\u5E8F\u5458\u81EA\u5B9A\u4E49\u6570\u636E\u7C7B\u578B\u7684\u6743\u5229\uFF0C\u4ECE\u800C\u5B9E\u73B0\u5BF9\u8BED\u8A00\u7684\u6269\u5C55\uFF1B\u4E8C\u662F\u5C06\u4E00\u4E9B\u6709\u7528\u7684\u529F\u80FD\u5C01\u88C5\u6210\u5E93\u51FD\u6570\u63D0\u4F9B\u7ED9\u7A0B\u5E8F\u5458\u3002</p><p>\u4E0E\u5927\u591A\u6570\u7F16\u7A0B\u8BED\u8A00\u4E00\u6837\uFF0CC++\u7684\u5BF9\u8C61\u7C7B\u578B\u51B3\u5B9A\u4E86\u80FD\u5BF9\u8BE5\u5BF9\u8C61\u8FDB\u884C\u7684\u64CD\u4F5C\uFF0C\u4E00\u6761\u8868\u8FBE\u5F0F\u662F\u5426\u5408\u6CD5\u4F9D\u8D56\u4E8E\u5176\u4E2D\u53C2\u4E0E\u8FD0\u7B97\u7684\u5BF9\u8C61\u7684\u7C7B\u578B\u3002\u4E00\u4E9B\u8BED\u8A00\uFF0C\u5982Smalltalk\u548CPython\u7B49\uFF0C\u5728\u7A0B\u5E8F\u8FD0\u884C\u65F6\u68C0\u67E5\u6570\u636E\u7C7B\u578B\uFF1B\u4E0E\u4E4B\u76F8\u53CD\uFF0CC++\u662F\u4E00\u79CD\u9759\u6001\u6570\u636E\u7C7B\u578B\u8BED\u8A00\uFF0C\u5B83\u7684\u7C7B\u578B\u68C0\u67E5\u53D1\u751F\u5728\u7F16\u8BD1\u65F6\u3002\u56E0\u6B64\uFF0C\u7F16\u8BD1\u5668\u5FC5\u987B\u77E5\u9053\u7A0B\u5E8F\u4E2D\u6BCF\u4E00\u4E2A\u53D8\u91CF\u5BF9\u5E94\u7684\u6570\u636E\u7C7B\u578B\u3002</p><p>C++\u63D0\u4F9B\u4E86\u4E00\u7EC4\u5185\u7F6E\u6570\u636E\u7C7B\u578B\u3001\u76F8\u5E94\u7684\u8FD0\u7B97\u7B26\u4EE5\u53CA\u4E3A\u6570\u4E0D\u591A\u7684\u51E0\u79CD\u7A0B\u5E8F\u6D41\u63A7\u5236\u8BED\u53E5\uFF0C\u8FD9\u4E9B\u5143\u7D20\u5171\u540C\u6784\u6210\u4E86C++\u8BED\u8A00\u7684\u57FA\u672C\u5F62\u6001\u3002\u4EE5\u8FD9\u4E9B\u5143\u7D20\u4E3A\u57FA\u7840\uFF0C\u6211\u4EEC\u53EF\u4EE5\u7F16\u5199\u51FA\u89C4\u6A21\u5E9E\u5927\u3001\u7ED3\u6784\u590D\u6742\u3001\u7528\u4E8E\u89E3\u51B3\u5B9E\u9645\u95EE\u9898\u7684\u8F6F\u4EF6\u7CFB\u7EDF\u3002\u4EC5\u5C31C++\u7684\u57FA\u672C\u5F62\u6001\u6765\u8BF4\uFF0C\u5B83\u662F\u4E00\u79CD\u7B80\u5355\u7684\u7F16\u7A0B\u8BED\u8A00\uFF0C\u5176\u5F3A\u5927\u7684\u80FD\u529B\u663E\u793A\u4E8E\u5B83\u5BF9\u7A0B\u5E8F\u5458\u81EA\u5B9A\u4E49\u6570\u636E\u7ED3\u6784\u7684\u652F\u6301\u3002\u8FD9\u79CD\u652F\u6301\u4F5C\u7528\u5DE8\u5927\uFF0C\u663E\u800C\u6613\u89C1\u7684\u4E00\u4E2A\u4E8B\u5B9E\u662F\uFF0CC++\u8BED\u8A00\u7684\u7F14\u9020\u8005\u65E0\u987B\u6D1E\u6089\u6240\u6709\u7A0B\u5E8F\u5458\u7684\u8981\u6C42\uFF0C\u800C\u7A0B\u5E8F\u5458\u6070\u597D\u53EF\u4EE5\u901A\u8FC7\u81EA\u4E3B\u5B9A\u4E49\u65B0\u7684\u6570\u636E\u7ED3\u6784\u6765\u4F7F\u8BED\u8A00\u6EE1\u8DB3\u4ED6\u4EEC\u5404\u81EA\u7684\u9700\u6C42\u3002</p><p>C++\u4E2D\u6700\u91CD\u8981\u7684\u8BED\u6CD5\u7279\u5F81\u5E94\u8BE5\u5C31\u662F\u7C7B\u4E86\uFF0C\u901A\u8FC7\u5B83\uFF0C\u7A0B\u5E8F\u5458\u53EF\u4EE5\u5B9A\u4E49\u81EA\u5DF1\u7684\u6570\u636E\u7C7B\u578B\u3002\u4E3A\u4E86\u4E0EC++\u7684\u5185\u7F6E\u7C7B\u578B\u533A\u522B\u5F00\u6765\uFF0C\u5B83\u4EEC\u901A\u5E38\u88AB\u79F0\u4E3A\u201C\u7C7B\u7C7B\u578B\uFF08class type\uFF09\u201D\u3002\u5728\u4E00\u4E9B\u7F16\u7A0B\u8BED\u8A00\u4E2D\uFF0C\u7A0B\u5E8F\u5458\u81EA\u5B9A\u4E49\u7684\u65B0\u7C7B\u578B\u4EC5\u80FD\u5305\u542B\u6570\u636E\u6210\u5458\uFF1B\u53E6\u5916\u4E00\u4E9B\u8BED\u8A00\uFF0C\u6BD4\u5982C++\uFF0C\u5219\u5141\u8BB8\u65B0\u7C7B\u578B\u4E2D\u65E2\u5305\u542B\u6570\u636E\u6210\u5458\uFF0C\u4E5F\u5305\u542B\u51FD\u6570\u6210\u5458\u3002C++\u8BED\u8A00\u4E3B\u8981\u7684\u4E00\u4E2A\u8BBE\u8BA1\u76EE\u6807\u5C31\u662F\u8BA9\u7A0B\u5E8F\u5458\u81EA\u5B9A\u4E49\u7684\u6570\u636E\u7C7B\u578B\u50CF\u5185\u7F6E\u7C7B\u578B\u4E00\u6837\u597D\u7528\u3002\u57FA\u4E8E\u6B64\uFF0C\u6807\u51C6C++\u5E93\u5B9E\u73B0\u4E86\u4E30\u5BCC\u7684\u7C7B\u548C\u51FD\u6570\u3002</p><p>\u672C\u4E66\u7B2C\u2160\u90E8\u5206\u7684\u4E3B\u9898\u662F\u5B66\u4E60C++\u8BED\u8A00\u7684\u57FA\u7840\u77E5\u8BC6\uFF0C\u8FD9\u4E5F\u662F\u638C\u63E1C++\u8BED\u8A00\u7684\u7B2C\u4E00\u6B65\u3002\u7B2C2\u7AE0\u8BE6\u8FF0\u5185\u7F6E\u7C7B\u578B\uFF0C\u5E76\u521D\u6B65\u4ECB\u7ECD\u4E86\u81EA\u5B9A\u4E49\u6570\u636E\u7C7B\u578B\u7684\u65B9\u6CD5\u3002\u7B2C3\u7AE0\u4ECB\u7ECD\u4E86\u4E24\u79CD\u6700\u57FA\u672C\u7684\u6570\u636E\u7C7B\u578B\uFF1A\u5B57\u7B26\u4E32\u548C\u5411\u91CF\u3002C++\u548C\u8BB8\u591A\u7F16\u7A0B\u8BED\u8A00\u6240\u5171\u6709\u7684\u4E00\u79CD\u5E95\u5C42\u6570\u636E\u7ED3\u6784\u2014\u2014\u6570\u7EC4\u4E5F\u5728\u672C\u7AE0\u6709\u6240\u63D0\u53CA\u3002\u63A5\u4E0B\u6765\uFF0C\u7B2C4\uFF5E6\u7AE0\u4F9D\u6B21\u4ECB\u7ECD\u4E86\u8868\u8FBE\u5F0F\u3001\u8BED\u53E5\u548C\u51FD\u6570\u3002\u4F5C\u4E3A\u7B2C\u2160\u90E8\u5206\u7684\u6700\u540E\u4E00\u7AE0\uFF0C\u7B2C7\u7AE0\u63CF\u8FF0\u4E86\u5982\u4F55\u6784\u5EFA\u6211\u4EEC\u81EA\u5DF1\u7684\u7C7B\uFF0C\u5B8C\u6210\u8FD9\u4E00\u4EFB\u52A1\u9700\u8981\u7EFC\u5408\u8FD0\u7528\u4E4B\u524D\u5404\u7AE0\u6240\u4ECB\u7ECD\u7684\u77E5\u8BC6\u3002</p><h2 id="\u7B2C2\u7AE0-\u53D8\u91CF\u548C\u57FA\u672C\u7C7B\u578B" tabindex="-1"><a class="header-anchor" href="#\u7B2C2\u7AE0-\u53D8\u91CF\u548C\u57FA\u672C\u7C7B\u578B" aria-hidden="true">#</a> \u7B2C2\u7AE0 \u53D8\u91CF\u548C\u57FA\u672C\u7C7B\u578B</h2><p>\u6570\u636E\u7C7B\u578B\u662F\u7A0B\u5E8F\u7684\u57FA\u7840\uFF1A\u5B83\u544A\u8BC9\u6211\u4EEC\u6570\u636E\u7684\u610F\u4E49\u4EE5\u53CA\u6211\u4EEC\u80FD\u5728\u6570\u636E\u4E0A\u6267\u884C\u7684\u64CD\u4F5C\u3002</p><p>C++\u8BED\u8A00\u652F\u6301\u5E7F\u6CDB\u7684\u6570\u636E\u7C7B\u578B\u3002\u5B83\u5B9A\u4E49\u4E86\u51E0\u79CD\u57FA\u672C\u5185\u7F6E\u7C7B\u578B\uFF08\u5982\u5B57\u7B26\u3001\u6574\u578B\u3001\u6D6E\u70B9\u6570\u7B49\uFF09\uFF0C\u540C\u65F6\u4E5F\u4E3A\u7A0B\u5E8F\u5458\u63D0\u4F9B\u4E86\u81EA\u5B9A\u4E49\u6570\u636E\u7C7B\u578B\u7684\u673A\u5236\u3002\u57FA\u4E8E\u6B64\uFF0CC++\u6807\u51C6\u5E93\u5B9A\u4E49\u4E86\u4E00\u4E9B\u66F4\u52A0\u590D\u6742\u7684\u6570\u636E\u7C7B\u578B\uFF0C\u6BD4\u5982\u53EF\u53D8\u957F\u5B57\u7B26\u4E32\u548C\u5411\u91CF\u7B49\u3002\u672C\u7AE0\u5C06\u4E3B\u8981\u8BB2\u8FF0\u5185\u7F6E\u7C7B\u578B\uFF0C\u5E76\u5E26\u9886\u5927\u5BB6\u521D\u6B65\u4E86\u89E3C++\u8BED\u8A00\u662F\u5982\u4F55\u652F\u6301\u66F4\u590D\u6742\u6570\u636E\u7C7B\u578B\u7684\u3002</p><p>\u6570\u636E\u7C7B\u578B\u51B3\u5B9A\u4E86\u7A0B\u5E8F\u4E2D\u6570\u636E\u548C\u64CD\u4F5C\u7684\u610F\u4E49\u3002\u5982\u4E0B\u6240\u793A\u7684\u8BED\u53E5\u662F\u4E00\u4E2A\u7B80\u5355\u793A\u4F8B\uFF1A</p><div class="language-cpp ext-cpp line-numbers-mode"><pre class="language-cpp"><code>i <span class="token operator">=</span> i <span class="token operator">+</span> j
</code></pre><div class="line-numbers"><span class="line-number">1</span><br></div></div><p>\u5176\u542B\u4E49\u4F9D\u8D56\u4E8Ei\u548Cj\u7684\u6570\u636E\u7C7B\u578B\u3002\u5982\u679Ci\u548Cj\u90FD\u662F\u6574\u578B\u6570\uFF0C\u90A3\u4E48\u8FD9\u6761\u8BED\u53E5\u6267\u884C\u7684\u5C31\u662F\u6700\u666E\u901A\u7684\u52A0\u6CD5\u8FD0\u7B97\u3002\u7136\u800C\uFF0C\u5982\u679Ci\u548Cj\u662FSales_item\u7C7B\u578B\u7684\u6570\u636E\uFF08\u53C2\u89C11.5.1\u8282\uFF0C\u7B2C17\u9875\uFF09\uFF0C\u5219\u4E0A\u8FF0\u8BED\u53E5\u628A\u8FD9\u4E24\u4E2A\u5BF9\u8C61\u7684\u6210\u5206\u76F8\u52A0\u3002</p><h3 id="_2-1-\u57FA\u672C\u5185\u7F6E\u7C7B\u578B" tabindex="-1"><a class="header-anchor" href="#_2-1-\u57FA\u672C\u5185\u7F6E\u7C7B\u578B" aria-hidden="true">#</a> 2.1 \u57FA\u672C\u5185\u7F6E\u7C7B\u578B</h3><p>C++\u5B9A\u4E49\u4E86\u4E00\u5957\u5305\u62EC\u7B97\u672F\u7C7B\u578B\uFF08arithmetic type\uFF09\u548C\u7A7A\u7C7B\u578B\uFF08void\uFF09\u5728\u5185\u7684\u57FA\u672C\u6570\u636E\u7C7B\u578B\u3002\u5176\u4E2D\u7B97\u672F\u7C7B\u578B\u5305\u542B\u4E86\u5B57\u7B26\u3001\u6574\u578B\u6570\u3001\u5E03\u5C14\u503C\u548C\u6D6E\u70B9\u6570\u3002\u7A7A\u7C7B\u578B\u4E0D\u5BF9\u5E94\u5177\u4F53\u7684\u503C\uFF0C\u4EC5\u7528\u4E8E\u4E00\u4E9B\u7279\u6B8A\u7684\u573A\u5408\uFF0C\u4F8B\u5982\u6700\u5E38\u89C1\u7684\u662F\uFF0C\u5F53\u51FD\u6570\u4E0D\u8FD4\u56DE\u4EFB\u4F55\u503C\u65F6\u4F7F\u7528\u7A7A\u7C7B\u578B\u4F5C\u4E3A\u8FD4\u56DE\u7C7B\u578B\u3002</p><h4 id="_2-1-1-\u7B97\u672F\u7C7B\u578B" tabindex="-1"><a class="header-anchor" href="#_2-1-1-\u7B97\u672F\u7C7B\u578B" aria-hidden="true">#</a> 2.1.1 \u7B97\u672F\u7C7B\u578B</h4><p>\u7B97\u672F\u7C7B\u578B\u5206\u4E3A\u4E24\u7C7B\uFF1A\u6574\u578B\uFF08integral type\uFF0C\u5305\u62EC\u5B57\u7B26\u548C\u5E03\u5C14\u7C7B\u578B\u5728\u5185\uFF09\u548C\u6D6E\u70B9\u578B\u3002 \u7B97\u672F\u7C7B\u578B\u7684\u5C3A\u5BF8\uFF08\u4E5F\u5C31\u662F\u8BE5\u7C7B\u578B\u6570\u636E\u6240\u5360\u7684\u6BD4\u7279\u6570\uFF09\u5728\u4E0D\u540C\u673A\u5668\u4E0A\u6709\u6240\u5DEE\u522B\u3002\u88682.1\u5217\u51FA\u4E86C++\u6807\u51C6\u89C4\u5B9A\u7684\u5C3A\u5BF8\u7684\u6700\u5C0F\u503C\uFF0C\u540C\u65F6\u5141\u8BB8\u7F16\u8BD1\u5668\u8D4B\u4E88\u8FD9\u4E9B\u7C7B\u578B\u66F4\u5927\u7684\u5C3A\u5BF8\u3002\u67D0\u4E00\u7C7B\u578B\u6240\u5360\u7684\u6BD4\u7279\u6570\u4E0D\u540C\uFF0C\u5B83\u6240\u80FD\u8868\u793A\u7684\u6570\u636E\u8303\u56F4\u4E5F\u4E0D\u4E00\u6837\u3002</p><table><thead><tr><th>\u7C7B\u578B</th><th>\u542B\u4E49</th><th>\u6700\u5C0F\u5C3A\u5BF8</th></tr></thead><tbody><tr><td>bool</td><td>\u5E03\u5C14\u7C7B\u578B</td><td>\u672A\u5B9A\u4E49</td></tr><tr><td>char</td><td>\u5B57\u7B26</td><td>8\u4F4D</td></tr><tr><td>wchar_t</td><td>\u5BBD\u5B57\u7B26</td><td>16\u4F4D</td></tr><tr><td>char16_t</td><td>Unicode\u5B57\u7B26</td><td>16\u4F4D</td></tr><tr><td>char32_t</td><td>Unicode\u5B57\u7B26</td><td>32\u4F4D</td></tr><tr><td>short</td><td>\u77ED\u6574\u578B</td><td>16\u4F4D</td></tr><tr><td>int</td><td>\u6574\u578B</td><td>16\u4F4D</td></tr><tr><td>long</td><td>\u957F\u6574\u578B</td><td>32\u4F4D</td></tr><tr><td>long long</td><td>\u957F\u6574\u578B</td><td>64\u4F4D</td></tr><tr><td>float</td><td>\u5355\u7CBE\u5EA6\u6D6E\u70B9\u6570</td><td>6\u4F4D\u6709\u6548\u6570\u5B57</td></tr><tr><td>double</td><td>\u53CC\u7CBE\u5EA6\u6D6E\u70B9\u6570</td><td>10\u4F4D\u6709\u6548\u6570\u5B57</td></tr><tr><td>long double</td><td>\u6269\u5C55\u7CBE\u5EA6\u6D6E\u70B9\u6570</td><td>10\u4F4D\u6709\u6548\u6570\u5B57</td></tr></tbody></table><p>\u5E03\u5C14\u7C7B\u578B\uFF08bool\uFF09\u7684\u53D6\u503C\u662F\u771F\uFF08true\uFF09\u6216\u8005\u5047\uFF08false\uFF09\u3002</p><p>C++\u63D0\u4F9B\u4E86\u51E0\u79CD\u5B57\u7B26\u7C7B\u578B\uFF0C\u5176\u4E2D\u591A\u6570\u652F\u6301\u56FD\u9645\u5316\u3002\u57FA\u672C\u7684\u5B57\u7B26\u7C7B\u578B\u662Fchar\uFF0C\u4E00\u4E2Achar\u7684\u7A7A\u95F4\u5E94\u786E\u4FDD\u53EF\u4EE5\u5B58\u653E\u673A\u5668\u57FA\u672C\u5B57\u7B26\u96C6\u4E2D\u4EFB\u610F\u5B57\u7B26\u5BF9\u5E94\u7684\u6570\u5B57\u503C\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u4E00\u4E2Achar\u7684\u5927\u5C0F\u548C\u4E00\u4E2A\u673A\u5668\u5B57\u8282\u4E00\u6837\u3002 \u5176\u4ED6\u5B57\u7B26\u7C7B\u578B\u7528\u4E8E\u6269\u5C55\u5B57\u7B26\u96C6\uFF0C\u5982wchar_t\u3001char16_t\u3001char32_t\u3002wchar_t\u7C7B\u578B\u7528\u4E8E\u786E\u4FDD\u53EF\u4EE5\u5B58\u653E\u673A\u5668\u6700\u5927\u6269\u5C55\u5B57\u7B26\u96C6\u4E2D\u7684\u4EFB\u610F\u4E00\u4E2A\u5B57\u7B26\uFF0C\u7C7B\u578Bchar16_t\u548Cchar32_t\u5219\u4E3AUnicode\u5B57\u7B26\u96C6\u670D\u52A1\uFF08Unicode\u662F\u7528\u4E8E\u8868\u793A\u6240\u6709\u81EA\u7136\u8BED\u8A00\u4E2D\u5B57\u7B26\u7684\u6807\u51C6\uFF09\u3002</p><p>\u9664\u5B57\u7B26\u548C\u5E03\u5C14\u7C7B\u578B\u4E4B\u5916\uFF0C\u5176\u4ED6\u6574\u578B\u7528\u4E8E\u8868\u793A\uFF08\u53EF\u80FD\uFF09\u4E0D\u540C\u5C3A\u5BF8\u7684\u6574\u6570\u3002C++\u8BED\u8A00\u89C4\u5B9A\u4E00\u4E2Aint\u81F3\u5C11\u548C\u4E00\u4E2Ashort\u4E00\u6837\u5927\uFF0C\u4E00\u4E2Along\u81F3\u5C11\u548C\u4E00\u4E2Aint\u4E00\u6837\u5927\uFF0C\u4E00\u4E2Along long\u81F3\u5C11\u548C\u4E00\u4E2Along\u4E00\u6837\u5927\u3002\u5176\u4E2D\uFF0C\u6570\u636E\u7C7B\u578Blong long\u662F\u5728C++11\u4E2D\u65B0\u5B9A\u4E49\u7684\u3002</p><p><strong>\u5185\u7F6E\u7C7B\u578B\u7684\u673A\u5668\u5B9E\u73B0</strong></p><blockquote><p>\u8BA1\u7B97\u673A\u4EE5\u6BD4\u7279\u5E8F\u5217\u5B58\u50A8\u6570\u636E\uFF0C\u6BCF\u4E2A\u6BD4\u7279\u975E0\u53731\uFF0C\u4F8B\u5982\uFF1A</p><p>00011011011100010110010000111011 ...</p><p>\u5927\u591A\u6570\u8BA1\u7B97\u673A\u4EE52\u7684\u6574\u6570\u6B21\u5E42\u4E2A\u6BD4\u7279\u4F5C\u4E3A\u5757\u6765\u5904\u7406\u5185\u5B58\uFF0C\u53EF\u5BFB\u5740\u7684\u6700\u5C0F\u5185\u5B58\u5757\u79F0\u4E3A\u201C\u5B57\u8282\uFF08byte\uFF09\u201D\uFF0C\u5B58\u50A8\u7684\u57FA\u672C\u5355\u5143\u79F0\u4E3A\u201C\u5B57\uFF08word\uFF09\u201D\uFF0C\u5B83\u901A\u5E38\u7531\u51E0\u4E2A\u5B57\u8282\u7EC4\u6210\u3002\u5728C++\u8BED\u8A00\u4E2D\uFF0C\u4E00\u4E2A\u5B57\u8282\u8981\u81F3\u5C11\u80FD\u5BB9\u7EB3\u673A\u5668\u57FA\u672C\u5B57\u7B26\u96C6\u4E2D\u7684\u5B57\u7B26\u3002\u5927\u591A\u6570\u673A\u5668\u7684\u5B57\u8282\u75318\u6BD4\u7279\u6784\u6210\uFF0C\u5B57\u5219\u753132\u621664\u6BD4\u7279\u6784\u6210\uFF0C\u4E5F\u5C31\u662F4\u62168\u5B57\u8282\u3002</p><p>\u5927\u591A\u6570\u8BA1\u7B97\u673A\u5C06\u5185\u5B58\u4E2D\u7684\u6BCF\u4E2A\u5B57\u8282\u4E0E\u4E00\u4E2A\u6570\u5B57\uFF08\u88AB\u79F0\u4E3A\u201C\u5730\u5740\uFF08address\uFF09\u201D\uFF09\u5173\u8054\u8D77\u6765\uFF0C\u5728\u4E00\u4E2A\u5B57\u8282\u4E3A8\u6BD4\u7279\u3001\u5B57\u4E3A32\u6BD4\u7279\u7684\u673A\u5668\u4E0A\uFF0C\u6211\u4EEC\u53EF\u80FD\u770B\u5230\u4E00\u4E2A\u5B57\u7684\u5185\u5B58\u533A\u57DF\u5982\u4E0B\u6240\u793A\uFF1A</p><p>\u6211\u4EEC\u80FD\u591F\u4F7F\u7528\u67D0\u4E2A\u5730\u5740\u6765\u8868\u793A\u4ECE\u8FD9\u4E2A\u5730\u5740\u5F00\u59CB\u7684\u5927\u5C0F\u4E0D\u540C\u7684\u6BD4\u7279\u4E32\uFF0C\u4F8B\u5982\uFF0C\u6211\u4EEC\u53EF\u80FD\u4F1A\u8BF4\u5730\u5740736424\u7684\u90A3\u4E2A\u5B57\u6216\u8005\u5730\u5740736427\u7684\u90A3\u4E2A\u5B57\u8282\u3002\u4E3A\u4E86\u8D4B\u4E88\u5185\u5B58\u4E2D\u67D0\u4E2A\u5730\u5740\u660E\u786E\u7684\u542B\u4E49\uFF0C\u5FC5\u987B\u9996\u5148\u77E5\u9053\u5B58\u50A8\u5728\u8BE5\u5730\u5740\u7684\u6570\u636E\u7684\u7C7B\u578B\u3002\u7C7B\u578B\u51B3\u5B9A\u4E86\u6570\u636E\u6240\u5360\u7684\u6BD4\u7279\u6570\u4EE5\u53CA\u8BE5\u5982\u4F55\u89E3\u91CA\u8FD9\u4E9B\u6BD4\u7279\u7684\u5185\u5BB9\u3002</p><p>\u5982\u679C\u4F4D\u7F6E736424\u5904\u7684\u5BF9\u8C61\u7C7B\u578B\u662Ffloat\uFF0C\u5E76\u4E14\u8BE5\u673A\u5668\u4E2Dfloat\u4EE532\u6BD4\u7279\u5B58\u50A8\uFF0C\u90A3\u4E48\u6211\u4EEC\u5C31\u80FD\u77E5\u9053\u8FD9\u4E2A\u5BF9\u8C61\u7684\u5185\u5BB9\u5360\u6EE1\u4E86\u6574\u4E2A\u5B57\u3002\u8FD9\u4E2Afloat\u6570\u7684\u5B9E\u9645\u503C\u4F9D\u8D56\u4E8E\u8BE5\u673A\u5668\u662F\u5982\u4F55\u5B58\u50A8\u6D6E\u70B9\u6570\u7684\u3002\u6216\u8005\u5982\u679C\u4F4D\u7F6E736424\u5904\u7684\u5BF9\u8C61\u7C7B\u578B\u662Funsigned char\uFF0C\u5E76\u4E14\u8BE5\u673A\u5668\u4F7F\u7528ISO-Latin-1\u5B57\u7B26\u96C6\uFF0C\u5219\u8BE5\u4F4D\u7F6E\u5904\u7684\u5B57\u8282\u8868\u793A\u4E00\u4E2A\u5206\u53F7\u3002</p></blockquote><p>\u6D6E\u70B9\u578B\u53EF\u8868\u793A\u5355\u7CBE\u5EA6\u3001\u53CC\u7CBE\u5EA6\u548C\u6269\u5C55\u7CBE\u5EA6\u503C\u3002C++\u6807\u51C6\u6307\u5B9A\u4E86\u4E00\u4E2A\u6D6E\u70B9\u6570\u6709\u6548\u4F4D\u6570\u7684\u6700\u5C0F\u503C\uFF0C\u7136\u800C\u5927\u591A\u6570\u7F16\u8BD1\u5668\u90FD\u5B9E\u73B0\u4E86\u66F4\u9AD8\u7684\u7CBE\u5EA6\u3002\u901A\u5E38\uFF0Cfloat\u4EE51\u4E2A\u5B57\uFF0832\u6BD4\u7279\uFF09\u6765\u8868\u793A\uFF0Cdouble\u4EE52\u4E2A\u5B57\uFF0864\u6BD4\u7279\uFF09\u6765\u8868\u793A\uFF0Clong double\u4EE53\u62164\u4E2A\u5B57\uFF0896\u6216128\u6BD4\u7279\uFF09\u6765\u8868\u793A\u3002\u4E00\u822C\u6765\u8BF4\uFF0C\u7C7B\u578Bfloat\u548Cdouble\u5206\u522B\u67097\u548C16\u4E2A\u6709\u6548\u4F4D\uFF1B\u7C7B\u578Blong double\u5219\u5E38\u5E38\u88AB\u7528\u4E8E\u6709\u7279\u6B8A\u6D6E\u70B9\u9700\u6C42\u7684\u786C\u4EF6\uFF0C\u5B83\u7684\u5177\u4F53\u5B9E\u73B0\u4E0D\u540C\uFF0C\u7CBE\u5EA6\u4E5F\u5404\u4E0D\u76F8\u540C\u3002</p><p><strong>\u5E26\u7B26\u53F7\u7C7B\u578B\u548C\u65E0\u7B26\u53F7\u7C7B\u578B</strong></p><p>\u9664\u53BB\u5E03\u5C14\u578B\u548C\u6269\u5C55\u7684\u5B57\u7B26\u578B\u4E4B\u5916\uFF0C\u5176\u4ED6\u6574\u578B\u53EF\u4EE5\u5212\u5206\u4E3A\u5E26\u7B26\u53F7\u7684\uFF08signed\uFF09\u548C\u65E0\u7B26\u53F7\u7684\uFF08unsigned\uFF09\u4E24\u79CD\u3002\u5E26\u7B26\u53F7\u7C7B\u578B\u53EF\u4EE5\u8868\u793A\u6B63\u6570\u3001\u8D1F\u6570\u62160\uFF0C\u65E0\u7B26\u53F7\u7C7B\u578B\u5219\u4EC5\u80FD\u8868\u793A\u5927\u4E8E\u7B49\u4E8E0\u7684\u503C\u3002</p><p>\u7C7B\u578Bint\u3001short\u3001long\u548Clong long\u90FD\u662F\u5E26\u7B26\u53F7\u7684\uFF0C\u901A\u8FC7\u5728\u8FD9\u4E9B\u7C7B\u578B\u540D\u524D\u6DFB\u52A0unsigned\u5C31\u53EF\u4EE5\u5F97\u5230\u65E0\u7B26\u53F7\u7C7B\u578B\uFF0C\u4F8B\u5982unsigned long\u3002\u7C7B\u578Bunsigned int\u53EF\u4EE5\u7F29\u5199\u4E3Aunsigned\u3002</p><p>\u4E0E\u5176\u4ED6\u6574\u578B\u4E0D\u540C\uFF0C\u5B57\u7B26\u578B\u88AB\u5206\u4E3A\u4E86\u4E09\u79CD\uFF1Achar\u3001signed char\u548Cunsigned char\u3002\u7279\u522B\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF1A\u7C7B\u578Bchar\u548C\u7C7B\u578Bsigned char\u5E76\u4E0D\u4E00\u6837\u3002\u5C3D\u7BA1\u5B57\u7B26\u578B\u6709\u4E09\u79CD\uFF0C\u4F46\u662F\u5B57\u7B26\u7684\u8868\u73B0\u5F62\u5F0F\u5374\u53EA\u6709\u4E24\u79CD\uFF1A\u5E26\u7B26\u53F7\u7684\u548C\u65E0\u7B26\u53F7\u7684\u3002\u7C7B\u578Bchar\u5B9E\u9645\u4E0A\u4F1A\u8868\u73B0\u4E3A\u4E0A\u8FF0\u4E24\u79CD\u5F62\u5F0F\u4E2D\u7684\u4E00\u79CD\uFF0C\u5177\u4F53\u662F\u54EA\u79CD\u7531\u7F16\u8BD1\u5668\u51B3\u5B9A\u3002</p><p>\u65E0\u7B26\u53F7\u7C7B\u578B\u4E2D\u6240\u6709\u6BD4\u7279\u90FD\u7528\u6765\u5B58\u50A8\u503C\uFF0C\u4F8B\u5982\uFF0C8\u6BD4\u7279\u7684unsigned char\u53EF\u4EE5\u8868\u793A0\u81F3255\u533A\u95F4\u5185\u7684\u503C\u3002</p><p>C++\u6807\u51C6\u5E76\u6CA1\u6709\u89C4\u5B9A\u5E26\u7B26\u53F7\u7C7B\u578B\u5E94\u5982\u4F55\u8868\u793A\uFF0C\u4F46\u662F\u7EA6\u5B9A\u4E86\u5728\u8868\u793A\u8303\u56F4\u5185\u6B63\u503C\u548C\u8D1F\u503C\u7684\u91CF\u5E94\u8BE5\u5E73\u8861\u3002\u56E0\u6B64\uFF0C8\u6BD4\u7279\u7684signed char\u7406\u8BBA\u4E0A\u5E94\u8BE5\u53EF\u4EE5\u8868\u793A-127\u81F3127\u533A\u95F4\u5185\u7684\u503C\uFF0C\u5927\u591A\u6570\u73B0\u4EE3\u8BA1\u7B97\u673A\u5C06\u5B9E\u9645\u7684\u8868\u793A\u8303\u56F4\u5B9A\u4E3A-128\u81F3127\u3002</p><p><strong>\u5EFA\u8BAE\uFF1A\u5982\u4F55\u9009\u62E9\u7C7B\u578B</strong></p><p>\u548CC\u8BED\u8A00\u4E00\u6837\uFF0CC++\u7684\u8BBE\u8BA1\u51C6\u5219\u4E4B\u4E00\u4E5F\u662F\u5C3D\u53EF\u80FD\u5730\u63A5\u8FD1\u786C\u4EF6\u3002C++\u7684\u7B97\u672F\u7C7B\u578B\u5FC5\u987B\u6EE1\u8DB3\u5404\u79CD\u786C\u4EF6\u7279\u8D28\uFF0C\u6240\u4EE5\u5B83\u4EEC\u5E38\u5E38\u663E\u5F97\u7E41\u6742\u800C\u4EE4\u4EBA\u4E0D\u77E5\u6240\u63AA\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u5927\u591A\u6570\u7A0B\u5E8F\u5458\u80FD\u591F\uFF08\u4E5F\u5E94\u8BE5\uFF09\u5BF9\u6570\u636E\u7C7B\u578B\u7684\u4F7F\u7528\u505A\u51FA\u9650\u5B9A\u4ECE\u800C\u7B80\u5316\u9009\u62E9\u7684\u8FC7\u7A0B\u3002\u4EE5\u4E0B\u662F\u9009\u62E9\u7C7B\u578B\u7684\u4E00\u4E9B\u7ECF\u9A8C\u51C6\u5219\uFF1A</p><ul><li>\u5F53\u660E\u786E\u77E5\u6653\u6570\u503C\u4E0D\u53EF\u80FD\u4E3A\u8D1F\u65F6\uFF0C\u9009\u7528\u65E0\u7B26\u53F7\u7C7B\u578B\u3002</li><li>\u4F7F\u7528int\u6267\u884C\u6574\u6570\u8FD0\u7B97\u3002\u5728\u5B9E\u9645\u5E94\u7528\u4E2D\uFF0Cshort\u5E38\u5E38\u663E\u5F97\u592A\u5C0F\u800Clong\u4E00\u822C\u548Cint\u6709\u4E00\u6837\u7684\u5C3A\u5BF8\u3002\u5982\u679C\u4F60\u7684\u6570\u503C\u8D85\u8FC7\u4E86int\u7684\u8868\u793A\u8303\u56F4\uFF0C\u9009\u7528long long\u3002</li><li>\u5728\u7B97\u672F\u8868\u8FBE\u5F0F\u4E2D\u4E0D\u8981\u4F7F\u7528char\u6216bool\uFF0C\u53EA\u6709\u5728\u5B58\u653E\u5B57\u7B26\u6216\u5E03\u5C14\u503C\u65F6\u624D\u4F7F\u7528\u5B83\u4EEC\u3002\u56E0\u4E3A\u7C7B\u578Bchar\u5728\u4E00\u4E9B\u673A\u5668\u4E0A\u662F\u6709\u7B26\u53F7\u7684\uFF0C\u800C\u5728\u53E6\u4E00\u4E9B\u673A\u5668\u4E0A\u53C8\u662F\u65E0\u7B26\u53F7\u7684\uFF0C\u6240\u4EE5\u5982\u679C\u4F7F\u7528char\u8FDB\u884C\u8FD0\u7B97\u7279\u522B\u5BB9\u6613\u51FA\u95EE\u9898\u3002\u5982\u679C\u4F60\u9700\u8981\u4F7F\u7528\u4E00\u4E2A\u4E0D\u5927\u7684\u6574\u6570\uFF0C\u90A3\u4E48\u660E\u786E\u6307\u5B9A\u5B83\u7684\u7C7B\u578B\u662Fsigned char\u6216\u8005unsigned char\u3002</li><li>\u6267\u884C\u6D6E\u70B9\u6570\u8FD0\u7B97\u9009\u7528double\uFF0C\u8FD9\u662F\u56E0\u4E3Afloat\u901A\u5E38\u7CBE\u5EA6\u4E0D\u591F\u800C\u4E14\u53CC\u7CBE\u5EA6\u6D6E\u70B9\u6570\u548C\u5355\u7CBE\u5EA6\u6D6E\u70B9\u6570\u7684\u8BA1\u7B97\u4EE3\u4EF7\u76F8\u5DEE\u65E0\u51E0\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u5BF9\u4E8E\u67D0\u4E9B\u673A\u5668\u6765\u8BF4\uFF0C\u53CC\u7CBE\u5EA6\u8FD0\u7B97\u751A\u81F3\u6BD4\u5355\u7CBE\u5EA6\u8FD8\u5FEB\u3002long double\u63D0\u4F9B\u7684\u7CBE\u5EA6\u5728\u4E00\u822C\u60C5\u51B5\u4E0B\u662F\u6CA1\u6709\u5FC5\u8981\u7684\uFF0C\u51B5\u4E14\u5B83\u5E26\u6765\u7684\u8FD0\u884C\u65F6\u6D88\u8017\u4E5F\u4E0D\u5BB9\u5FFD\u89C6\u3002</li></ul><h5 id="_2-1-1\u8282\u7EC3\u4E60" tabindex="-1"><a class="header-anchor" href="#_2-1-1\u8282\u7EC3\u4E60" aria-hidden="true">#</a> 2.1.1\u8282\u7EC3\u4E60</h5><p>\u7EC3\u4E602.1\uFF1A\u7C7B\u578Bint\u3001long\u3001long long\u548Cshort\u7684\u533A\u522B\u662F\u4EC0\u4E48\uFF1F\u65E0\u7B26\u53F7\u7C7B\u578B\u548C\u5E26\u7B26\u53F7\u7C7B\u578B\u7684\u533A\u522B\u662F\u4EC0\u4E48\uFF1Ffloat\u548Cdouble\u7684\u533A\u522B\u662F\u4EC0\u4E48\uFF1F</p><p>\u7EC3\u4E602.2\uFF1A\u8BA1\u7B97\u6309\u63ED\u8D37\u6B3E\u65F6\uFF0C\u5BF9\u4E8E\u5229\u7387\u3001\u672C\u91D1\u548C\u4ED8\u6B3E\u5206\u522B\u5E94\u9009\u62E9\u4F55\u79CD\u6570\u636E\u7C7B\u578B\uFF1F\u8BF4\u660E\u4F60\u7684\u7406\u7531\u3002</p><h2 id="\u7B2C3\u7AE0-\u5B57\u7B26\u4E32\u3001\u5411\u91CF\u548C\u6570\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u7B2C3\u7AE0-\u5B57\u7B26\u4E32\u3001\u5411\u91CF\u548C\u6570\u7EC4" aria-hidden="true">#</a> \u7B2C3\u7AE0 \u5B57\u7B26\u4E32\u3001\u5411\u91CF\u548C\u6570\u7EC4</h2><h2 id="\u7B2C4\u7AE0-\u8868\u8FBE\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u7B2C4\u7AE0-\u8868\u8FBE\u5F0F" aria-hidden="true">#</a> \u7B2C4\u7AE0 \u8868\u8FBE\u5F0F</h2><h2 id="\u7B2C5\u7AE0-\u8BED\u53E5" tabindex="-1"><a class="header-anchor" href="#\u7B2C5\u7AE0-\u8BED\u53E5" aria-hidden="true">#</a> \u7B2C5\u7AE0 \u8BED\u53E5</h2><h2 id="\u7B2C6\u7AE0-\u51FD\u6570" tabindex="-1"><a class="header-anchor" href="#\u7B2C6\u7AE0-\u51FD\u6570" aria-hidden="true">#</a> \u7B2C6\u7AE0 \u51FD\u6570</h2><h2 id="\u7B2C7\u7AE0-\u7C7B" tabindex="-1"><a class="header-anchor" href="#\u7B2C7\u7AE0-\u7C7B" aria-hidden="true">#</a> \u7B2C7\u7AE0 \u7C7B</h2>`,42);function e(n,i){return r}var o=d(a,[["render",e]]);export{o as default};
